INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243332, 'Ruxolitinib', 'Osilodrostat', 'Minor', 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with mild or moderate inhibitors of CYP450 3A4 may modestly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.  In healthy subjects, administration of a single 10 mg dose of ruxolitinib following pretreatment with the moderate CYP450 3A4 inhibitor erythromycin (500 mg twice daily for four days) resulted in an 8% increase in ruxolitinib peak plasma concentration (Cmax) and a 27% increase in systemic exposure (AUC) compared to administration of ruxolitinib alone.  The change in the pharmacodynamic marker, pSTAT3 inhibition, was consistent with the corresponding exposure information.  No dosage adjustment is recommended when ruxolitinib is coadministered with mild or moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/280360/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation  (2011):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243333, 'Sildenafil', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.  Dosage adjustments may be appropriate for sildenafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects.  Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/280361/', '[1] "Product Information. Viagra (sildenafil)." Pfizer U.S. Pharmaceuticals  (2001):[2] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2000):  392-7[3] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001):  239-48[4] "Product Information. Revatio (sildenafil)." Pfizer U.S. Pharmaceuticals Group  (2005):', 1767363028);
