INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243430, 'Mitotane', 'Lapatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, a gradual dosage increase of lapatinib should be considered.  The prescribing information recommends titrating the lapatinib dosage gradually from 1250 mg/day up to 4500 mg/day or from 1500 mg/day up to 5500 mg/day depending on the indication and patient tolerability.  Based on pharmacokinetic studies, this dosage recommendation is predicted to adjust the lapatinib systemic exposure (AUC) to the range observed without inducers.  However, clinical data are lacking.  The dosage of lapatinib should be reduced over approximately 2 weeks to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281410/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals  (2023):[2] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals Canada Inc  (2022):[3] "Product Information. Tyverb (lapatinib)." Novartis Pharmaceuticals UK Ltd  (2022):[4] "Product Information. Tykerb (laPAtinib)." Novartis Pharmaceuticals Australia Pty Ltd  (2022):[5] Smith DA, Koch KM, Arya N, Bowen CJ, Herendeen JM, Beelan A "Effects of ketoconazole and carbamazepine on lapatinib pharmacokinetics in healthy subjects." Br J Clin Pharmacol 67 (2009):  421-6', 1767363028);
