INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243461, 'Anisindione', 'Penbutolol', 'Minor', 'Some oral beta-blockers may increase serum levels of oral anticoagulants and enhance anticoagulant effects.  The mechanism is unknown .  Data have been conflicting.  Case reports exist for propranolol, while acebutolol, atenolol, betaxolol, bisoprolol, carvedilol, esmolol, metoprolol, and pindolol reportedly do not interact.  Hypoprothrombinemic effects are expected to be minimal.', NULL, 'It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant.', NULL, 'It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/281835/', '[1] Ku LL, Ward CO, Durgin SJ "A clinical study of drug interaction and anticoagulant therapy." Drug Intell Clin Pharm 4 (1970):  300-6[2] Ryan JR "Clinical pharmacology of acebutolol." Am Heart J 109 (1985):  1131-6[3] Bax ND, Lennard MS, Tucker GT, et al. "The effect of beta-adrenoceptor antagonists on the pharmacokinetics and pharmacodynamics of warfarin after a single dose." Br J Clin Pharmacol 17 (1984):  553-7[4] Spahn H, Kirch W, Mutschler E