INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243541, 'Olaparib', 'Melphalan flufenamide', 'Moderate', 'Coadministration with other myelosuppressive agents may potentiate and prolong the bone marrow toxicity associated with olaparib.', NULL, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.', NULL, 'Caution is advised if olaparib is prescribed with other myelosuppressive agents.  Complete blood counts should be performed at baseline and monthly thereafter.  Do not start olaparib until patients have recovered from hematologic toxicity caused by previous chemotherapy.  For prolonged hematologic toxicities, olaparib should be interrupted and blood counts monitored weekly until recovery.  If the levels have not recovered to CTCAE Grade 1 or less after 4 weeks, a haematologist should be consulted for further investigations, including bone marrow analysis and blood sample for cytogenetics.  If MDS/AML is confirmed, discontinue olaparib.  Patients should be advised to contact their physician if they experience weakness, fatigue, fever, weight loss, infections, shortness of breath, unusual bleeding or bruising, or blood in urine or stool.', 'Synergism', 'Pertuzumab, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/283172/', '[1] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 1767369485);
