INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243747, 'Bepridil', 'Telaprevir', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitor, telaprevir, may significantly increase the plasma concentrations of certain antiarrhythmic agents such as amiodarone, bepridil, disopyramide, and quinidine.  The mechanism involves inhibition of CYP450 3A4 metabolism, as telaprevir is a potent inhibitor.', NULL, 'Caution is advised if telaprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4.', NULL, 'Caution is advised if telaprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4.  Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/284348/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals  (2011):', 1767369485);
