INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243945, 'Anisindione', 'Interferon alfa-2a, Recombinant', 'Moderate', 'Interferons may increase the plasma concentrations and pharmacologic effects of warfarin and other oral anticoagulants.  At least one case of this interaction has been reported in the literature.  The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes.  Physiologic events and drugs that induce interferon synthesis (e.g., viral infection, influenza vaccination) have also been reported to decrease metabolism and potentiate the effects of certain drugs including warfarin.', NULL, 'Caution is advised if oral anticoagulants are administered with an interferon.', NULL, 'Caution is advised if oral anticoagulants are administered with an interferon.  The INR should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/285489/', '[1] Kramer P, Tsuru M, Cook CE, et al. "Effect of influenza vaccine on warfarin anticoagulation." Clin Pharmacol Ther 35 (1984):  416-8[2] Okuno H, Takasu M, Kano H, Seki T, Shiozaki Y, Inoue K "Depression of drug-metabolizing activity in the human liver by interferon-beta." Hepatology 17 (1993):  65-9[3] Adachi Y, Yokoyama Y, Nanno T, Yamamoto T "Potentiation of warfarin by interferon." BMJ 311 (1995):  292[4] Sakai H, Okamoto T, Kikkawa Y "Suppression of hepatic drug metabolism by the interferon inducer polyriboinosinic acid:polyribocitidylic acid." J Pharmacol Exp Ther 263 (1992):  381-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (243946, 'Interferon alfa-2a, Recombinant', 'Aurothioglucose', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect.', NULL, 'Caution is advised during concomitant use of agents with neurotoxic effects.', NULL, 'Caution is advised during concomitant use of agents with neurotoxic effects.  Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.  Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded.  Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.  If feasible, therapy should generally be reinstituted only after resolution of neuropathy symptoms or return of symptoms to baseline status.  In some cases, permanent dosage reductions may be required.', 'Synergism', 'Penicillamine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/285490/', '[1] Carrion C, Espinosa E, Herrero A, Garcia B "Possible vincristine-isoniazid interaction." Ann Pharmacother 29 (1995):  201[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979):  663-6[3] Pharmaceutical Society of Australia "APPGuide online. Australian prescription products guide online.  http://www.appco.com.au/appguide/default.asp"   (2006):[4] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports.  http://www.ema.europa.eu/ema/index.jsp?c