INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244069, 'Enoxacin', 'Melatonin', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may substantially increase the plasma concentrations of melatonin.  Data from available studies suggest that melatonin is primarily metabolized by CYP450 1A2, with possible contribution from CYP450 2C19 and 2C9.  Coadministration of fluvoxamine, a potent CYP450 1A2 inhibitor that also inhibits CYP450 2C19 and 2C9, increased melatonin systemic exposure (AUC) by 17-fold and maximum plasma concentration (Cmax) by 12-fold.  Contraceptives containing ethinyl estradiol, which can inhibit CYP450 1A2, may lead to a 4- to 5-fold increase in melatonin concentration.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.', NULL, 'Caution is advised when melatonin is used concomitantly with CYP450 1A2 inhibitors.  Patients should be monitored closely for adverse effects such as excessive drowsiness, headache, lethargy, dizziness, irritability, nervousness, restlessness, anxiety, abdominal pain, and dyspepsia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/286222/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
