INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244250, 'Anisindione', 'Rofecoxib', 'Moderate', 'Rofecoxib may potentiate the hypoprothrombinemic effect of warfarin and other oral anticoagulants as determined by the INR.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and rofecoxib.', NULL, 'Patients should be closely monitored during concomitant therapy with oral anticoagulants and rofecoxib.  The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rofecoxib in patients who are stabilized on their anticoagulant regimen.  Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/287654/', '[1] "Product Information. Vioxx (rofecoxib)." Merck & Co., Inc  (2001):[2] Schwartz JI, Bugianesi KJ, Ebel DL, DeSmet M, Haesen R, Larson PJ, Ko A, Verbesselt R, Hunt TL, Lins R, Lens S, Porras AG "The effect of rofecoxib on the pharmacodynamics and pharmacokinetics of warfarin." Clin Pharmacol Ther 68 (2000):  626-36[3] Stading JA, Skrabal MZ, Faulkner MA "Seven cases of interaction between warfarin and cyclooxygenase-2 inhibitors." Am J Health Syst Pharm 58 (2001):  2076-80', 1767363028);
