INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244365, 'Abametapir (topical)', 'Lopinavir', 'Moderate', 'Based on in vitro inhibition data, a single topical application of abametapir lotion may increase plasma concentrations of drugs that are substrates of CYP450 3A4, CYP450 2B6, and CYP450 1A2 isoenzymes and increase their systemic concentrations.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.', NULL, 'Use of CYP450 3A4, CYP450 2B6, and CYP450 1A2 substrates should generally be avoided within 2 weeks after topical application of abametapir lotion.  If this is not feasible, the manufacturer recommends avoiding use of abametapir lotion.  If abametapir is used, monitor for increased toxicity of the CYP450 3A4, CYP450 2B6, and/or CYP450 1A2 substrate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288165/', '[1] "Product Information. Xeglyze (abametapir topical)." Dr. Reddy''s Laboratories Inc  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244366, 'Abarelix', 'Lopinavir', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.  Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288166/', '[1] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc  (2002):[2] "Product Information. Zoladex (goserelin)." Astra-Zeneca Pharmaceuticals  (2001):[3] "Product Information. Trelstar (triptorelin)." Pharmacia and Upjohn  (2001):[4] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):[5] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc  (2003):[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc)  (2010):[8] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc  (2013):', 1767363028);
