INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244371, 'Adenosine', 'Lopinavir', 'Moderate', 'Adenosine has induced torsade de pointes arrhythmia in patients with preexisting long QT syndrome.  Theoretically, coadministration of adenosine with agents that can prolong the QT interval may increase that risk as well.  The mechanism has not been established, but may involve the depressant effect of adenosine on the sinoatrial and atrioventricular nodes.', NULL, 'Adenosine should be used with caution in patients receiving drugs that may prolong the QT interval.', NULL, 'Adenosine should be used with caution in patients receiving drugs that may prolong the QT interval.  Adenosine should be discontinued immediately if severe bradycardia occurs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288172/', '[1] Wesley RC Jr, Turnquest P "Torsades de pointe after intravenous adenosine in the presence of prolonged QT syndrome." Am Heart J 123 (1992):  794-6[2] Tachakra SS, Robinson S "Adenosine and cardiac arrhythmias." BMJ 305 (1992):  422[3] Celiker A, Tokel K, Cil E, Ozkutlu S, Ozme S "Adenosine induced torsades de pointes in a child with congenital long QT syndrome." Pacing Clin Electrophysiol 17 (1994):  1814-7[4] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000):  49-75[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] Harrington GR, Froelich EG "Adenosine-induced torsades de pointes." Chest 103 (1993):  1299-1301[9] Mallet ML "Proarrhythmic effects of adenosine: a review of the literature." Emerg Med J 21 (2004):  408-10[10] Drescher MJ, Mendelssohn R "Transient torsades de pointes after adenosine." Isr J Trauma Intensive Care Emerg Med 2 (2002):  15-7', 1767363028);
