INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244374, 'Alectinib', 'Lopinavir', 'Moderate', 'Coadministration of alectinib with other agents that can slow the heart rate may increase the risk of bradycardia.', NULL, 'Caution is advised when alectinib is prescribed with other drugs that can cause bradycardia (e.', NULL, 'Caution is advised when alectinib is prescribed with other drugs that can cause bradycardia (e.g., beta-blockers; calcium channel blockers; digitalis; dolasetron; flecainide; ivabradine; lacosamide; mefloquine; moricizine; propafenone; quinine; succinylcholine; sunitinib; thalidomide; anticholinesterase or cholinergic agents; protease inhibitors such as atazanavir, lopinavir/ritonavir, and saquinavir/ritonavir).  Heart rate and blood pressure should be monitored regularly, and patients should be counseled to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.  Dosage modification of alectinib is not required in cases of asymptomatic bradycardia.  However, if symptomatic bradycardia occurs, the manufacturer recommends withholding alectinib until recovery to asymptomatic bradycardia or to a heart rate of >=60 bpm.  Evaluate concomitant medications known to cause bradycardia and all antihypertensive medications, then consider dosage adjustments or discontinuation of concomitant medication(s) or alectinib in accordance with the product labeling.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288175/', '[1] "Product Information. Alecensa (alectinib)." Genentech  (2015):', 1767363028);
