INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244384, 'Amprenavir', 'Lopinavir', 'Major', 'Coadministration with amprenavir may decrease the plasma concentrations of lopinavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster.  The proposed mechanism is amprenavir induction of lopinavir metabolism via CYP450 3A4.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir.', NULL, 'A dosage increase is recommended for lopinavir-ritonavir when used in combination with amprenavir.  For adults, the dosage should be increased to 500 mg-125 mg (two 200 mg-50 mg tablets and one 100 mg-25 mg tablet) twice daily for the tablets and 533 mg-133 mg (6.5 mL) twice daily for the oral solution.  Lopinavir-ritonavir should not be administered as a once daily regimen in combination with amprenavir.  For pediatric patients 6 months to 18 years of age, the dosage should be increased to 300 mg-75 mg/m2 twice daily for the oral solution in both treatment-naive and treatment-experienced patients, not to exceed the recommended adult dosage of 533 mg-133 mg (6.5 mL) twice daily.  If weight-based dosing is preferred, the recommended dosage is 13 mg-3.25 mg/kg given twice daily for patients under 15 kg and 11 mg-2.75 mg/kg given twice daily for patients 15 kg to 45 kg.  Pediatric patients weighing more than 45 kg should receive the adult dosage of lopinavir-ritonavir.  Please consult the manufacturer''s product labeling for pediatric dosing of the tablet formulation.  Lopinavir-ritonavir should not be administered in combination with amprenavir in patients under 6 months of age due to the lack of clinical data.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288185/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[2] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244385, 'Anagrelide', 'Lopinavir', 'Major', 'Anagrelide can cause dose-dependent prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.  Patients receiving anagrelide should have a cardiovascular examination, including an ECG, prior to initiation of treatment and monitored for cardiovascular effects during treatment.  Hypokalemia or hypomagnesemia must be corrected prior to therapy and electrolytes monitored periodically during therapy.  Anagrelide should not be used in the presence of known risk factors for QT interval prolongation such as congenital long QT syndrome or a history of acquired QTc prolongation.  Periodic electrocardiograms are recommended in patients with heart failure, bradyarrhythmias, or electrolyte abnormalities.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288186/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
