INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244416, 'Carbamazepine', 'Lopinavir', 'Major', 'Coadministration of lopinavir-ritonavir with carbamazepine may result in decreased plasma concentrations of lopinavir and increased plasma concentrations of carbamazepine.  The proposed mechanism involves carbamazepine induction of lopinavir metabolism via CYP450 3A4 and conversely, lopinavir-ritonavir inhibition of carbamazepine metabolism via the same enzymatic pathway.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with carbamazepine.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with carbamazepine.  Once-daily administration of lopinavir-ritonavir should be avoided when used concomitantly with carbamazepine.  An increase in the dosage of lopinavir-ritonavir may be necessary, although dosage adjustment has not been evaluated in clinical studies.  Close clinical and laboratory monitoring of antiretroviral response is recommended following the addition or discontinuation of carbamazepine in patients already receiving lopinavir-ritonavir as part of their HIV treatment regimen.  Likewise, if lopinavir-ritonavir is added to stable carbamazepine therapy, serum drug levels and pharmacologic effects should be closely monitored and the carbamazepine dose adjusted accordingly.  Patients should be advised to contact their doctor if they develop signs of carbamazepine toxicity such as ataxia, disorientation, dizziness, nausea, vomiting, and visual disturbances.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288226/', '[1] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals  (2002):[2] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Bates DE, Herman RJ "Carbamazepine toxicity induced by lopinavir/ritonavir and nelfinavir." Ann Pharmacother 40 (2006):  1190-5[5] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Department of Health and
Human Services "Guidelines for the Use of Antiretroviral Agents in
HIV-1-Infected Adults and Adolescents.  https://aidsinfo.nih.gov/contentfiles/lvguidelines/AdultAndAdolescentGL.pdf"   (2015):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244417, 'Carteolol', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme.  The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288227/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):', 1767363028);
