INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244421, 'Cenobamate', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288233/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[3] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[4] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004):  482-9[5] Lim ML, Min SS, Eron JJ, et al. "Coadministration of lopinavir/ritonavir and phenytoin results in two-way drug interaction through cytochrome P-450 induction." J Acquir Immune Defic Syndr 36 (2004):  1034-40', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244422, 'Ceritinib', 'Lopinavir', 'Major', 'Ceritinib can cause concentration-dependent prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if ceritinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if ceritinib is used in combination with other drugs that can prolong the QT interval.  ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting ceritinib therapy and periodically during treatment.  Ceritinib should not be started if baseline QTc is greater than 500 msec.  Likewise, treatment should be withheld in patients who develop a QTc interval greater than 500 msec on at least 2 separate ECGs until the QTc interval is less than 481 msec or recovery to baseline (if the QTc interval is greater than or equal to 481 msec), then resume ceritinib with a 150 mg dosage reduction.  Permanently discontinue ceritinib therapy if QTc prolongation develops in combination with torsade de pointes or polymorphic ventricular tachycardia or signs and symptoms of serious arrhythmia.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/int