INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244440, 'Crizotinib', 'Lopinavir', 'Major', 'Crizotinib can cause concentration-dependent prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if crizotinib is used in combination with other drugs that can prolong the QT interval.  ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting crizotinib therapy and periodically during treatment.  Crizotinib should not be started if baseline QTc is greater than 500 msec.  Likewise, treatment should be interrupted in patients who develop Grade 3 QTc prolongation until recovery to less than or equal to Grade 1, then resumed at 200 mg twice daily.  In case of recurrence of Grade 3 QTc prolongation, therapy should be withheld until recovery to less than or equal to Grade 1, then resumed at 250 mg once daily.  Permanently discontinue crizotinib therapy if Grade 3 QTc prolongation recurs or at any time during treatment if Grade 4 QTc prolongation develops.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288252/', '[1] "Product Information. Xalkori (crizotinib)." Pfizer U.S. Pharmaceuticals Group  (2011):', 1767363028);
