INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244446, 'Darunavir', 'Lopinavir', 'Moderate', 'Coadministration with lopinavir/ritonavir may decrease the plasma concentrations of darunavir, even in the presence of low-dose ritonavir as a pharmacokinetic booster.  The mechanism of interaction has not been described.', NULL, 'The appropriate dosage of darunavir when prescribed in combination with lopinavir/ritonavir has not been established.', NULL, 'The appropriate dosage of darunavir when prescribed in combination with lopinavir/ritonavir has not been established.  Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of darunavir with lopinavir/ritonavir is not recommended, with or without an additional low-dose of ritonavir.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288258/', '[1] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc  (2006):', 1767363028);
