INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244455, 'Dexamethasone', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288267/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[3] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[4] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004):  482-9[5] Lim ML, Min SS, Eron JJ, et al. "Coadministration of lopinavir/ritonavir and phenytoin results in two-way drug interaction through cytochrome P-450 induction." J Acquir Immune Defic Syndr 36 (2004):  1034-40', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244456, 'Didanosine', 'Lopinavir', 'Moderate', 'Since lopinavir-ritonavir oral solution must be taken with food, simultaneous administration with didanosine may interfere with the gastrointestinal absorption of didanosine, which should be taken on an empty stomach.', NULL, 'When prescribed in combination with lopinavir-ritonavir oral solution, didanosine should be administered on an empty stomach one hour before or two hours after lopinavir-ritonavir oral solution, which is to be taken with food.', NULL, 'When prescribed in combination with lopinavir-ritonavir oral solution, didanosine should be administered on an empty stomach one hour before or two hours after lopinavir-ritonavir oral solution, which is to be taken with food.  Lopinavir-ritonavir tablets can be administered simultaneously with didanosine without food.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288268/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[2] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244457, 'Digitoxin', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme.  The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288269/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):', 1767363028);
