INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244460, 'Disopyramide', 'Lopinavir', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.  Caution and clinical monitoring are recommended if concomitant use is required.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288272/', '[1] "Product Information. Cordarone (amiodarone)." Wyeth-Ayerst Laboratories  (2002):[2] "Product Information. Vascor (bepridil)." McNeil Pharmaceutical  (2002):[3] "Product Information. Procan SR (procainamide)." Parke-Davis  (2001):[4] "Product Information. Quiniglute (quinidine)." Berlex, Richmond, CA.[5] "Product Information. Betapace (sotalol)." Berlex Laboratories  (2001):[6] "Product Information. Norpace (disopyramide)." Searle  (2001):[7] Trujillo TC, Nolan PE "Antiarrhythmic agents - Drug interactions of clinical significance." Drug Safety 23 (2000):  509-32[8] Yamreudeewong W, DeBisschop M, Martin L, Lower D "Potentially Significant Drug Interactions of Class III Antiarrhythmic Drugs." Drug Saf 26 (2003):  421-38[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[11] Cerner Multum, Inc. "Australian Product Information." O 0[12] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):[13] Maxa JL, Hebeler RF, Adeeko MA "Torsades de pointes following concurrent amiodarone and levofloxacin therapy." Proc (Bayl Univ Med Cent) 19 (2006):  345-6', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244461, 'Valproic acid', 'Lopinavir', 'Moderate', 'Coadministration of protease inhibitors (e.g., lopinavir, ritonavir) with valproate may result in a reduction in the plasma concentrations and therapeutic effects of valproate.  The proposed mechanism may involve protease inhibitor induction of hepatic CYP450 enzymes, particularly glucuronosyltransferases, which increase metabolism of valproate.  This interaction has been reported with ritonavir and ritonavir-boosted lopinavir.', NULL, 'Caution is advised if valproate is used concomitantly with the protease inhibitors ritonavir and lopinavir.', NULL, 'Caution is advised if valproate is used concomitantly with the protease inhibitors ritonavir and lopinavir.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever protease inhibitors are added to or withdrawn from therapy with valproate.  Patients should be monitored for loss of seizure control.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288273/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244462, 'Dofetilide', 'Lopinavir', 'Major', 'Like other class III antiarrhythmic agents, dofetilide can cause dose-related QT interval prolongation.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', NULL, 'Coadministration of dofetilide with other drugs that can prolong the QT interval has not been studied and is not recommended.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288274/', '[1] "Product Information. Tikosyn (dofetilide)." Pfizer U.S. Pharmaceuticals  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[4] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244463, 'Dolasetron', 'Lopinavir', 'Major', 'Dolasetron can cause dose-related prolongation of the QT interval via its pharmacologically active metabolite, hydrodolasetron.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the QT, PR and/or QRS interval, especially in the elderly and patients with known risk factors.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the QT, PR and/or QRS interval, especially in the elderly and patients with known risk factors.  An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment.  Patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.  Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288275/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion Roussel  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244464, 'Donepezil', 'Lopinavir', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.  Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288276/', '[1] "Product Information. Cognex (tacrine)." Parke-Davis  (2001):[2] "Product Information. Aricept (donepezil)." Pfizer U.S. Pharmaceuticals  (2001):[3] "Product Information. Exelon (rivastigmine)." Novartis Pharmaceuticals  (2001):[4] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc  (2005):[5] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007):  1063-71[6] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998):  40-4', 1767363028);
