INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244477, 'Encorafenib', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir may cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.  Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288290/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[2] Anson BD, Weaver JG, Ackerman MJ, et al. "Blockade of HERG channels by HIV protease inhibitors." Lancet 365 (2005):  682-686[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244478, 'Entrectinib', 'Lopinavir', 'Moderate', 'Entrectinib may cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of entrectinib with other drugs that can prolong the QT interval should generally be avoided.  Caution and clinical monitoring are recommended if concomitant use is required.  Patients should have electrocardiograms (ECGs) performed prior to initiation of entrectinib, after 1 month of treatment with entrectinib, and periodically during treatment as appropriate based on individual risk factors.  Some authorities suggest that if the QTc interval is between 481 to 500 msec at any time during treatment, entrectinib should be withheld until QTc is recovered to baseline at which time treatment at the same dose may be resumed (AU, UK).  If the QTc is greater than 500 msec, entrectinib should be withheld until the QTc interval recovers to baseline at which time the same dose may be resumed if factors that caused the QT prolongation are corrected.  However, if these factors are not identified and corrected, entrectinib should be resumed at a reduced dose.  Entrectinib should be permanently discontinued in patients who develop QTc interval prolongation with signs or symptoms of life-threatening arrhythmia such as torsade de pointes or polymorphic ventricular tachycardia.  Because hypokalemia and hypomagnesemia are risk factors for ventricular arrhythmias, electrolyte levels should also be obtained prior to and during treatment, and any abnormalities corrected as necessary.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288291/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244479, 'Enzalutamide', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288292/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[3] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[4] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004):  482-9[5] Lim ML, Min SS, Eron JJ, et al. "Coadministration of lopinavir/ritonavir and phenytoin results in two-way drug interaction through cytochrome P-450 induction." J Acquir Immune Defic Syndr 36 (2004):  1034-40', 1767363028);
