INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470434, 'Triazolam', 'Olaparib', 'Moderate', 'Based on in vitro inhibition data, coadministration with olaparib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 (e.g., cisapride, ergot alkaloids, fentanyl, lovastatin, oral midazolam, pimozide, quetiapine, simvastatin, triazolam, vinca alkaloids), P-gp (e.g., colchicine, dabigatran, digoxin), breast cancer resistance protein (BCRP) (e.g., rosuvastatin), OATP1B1 (e.g., eluxadoline, glyburide, repaglinide, statins, valsartan), or organic cation transporter 1 or 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism is decreased clearance due to inhibition of the corresponding metabolizing enzyme and/or efflux/uptake transporter by olaparib.', 'DDInter', 1767317476, 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever olaparib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470435, 'Trichophyton mentagrophytes', 'Olaparib', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
