INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470458, 'Zileuton', 'Olaparib', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 1A2 and/or 2B6. The proposed mechanism, based on in vitro data, is increased clearance due to olaparib-mediated induction of CYP450 1A2 and/or 2B6.', 'DDInter', 1767317476, 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6. Pharmacologic response and serum levels should be monitored more closely whenever olaparib is added to or withdrawn from therapy with these drugs. Dosage adjustments may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised if olaparib is used concomitantly with drugs that are substrates of CYP450 1A2 and/or 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
