INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470530, 'Fesoterodine', 'Oliceridine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 1767317476, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470531, 'Fingolimod', 'Oliceridine', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317476, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
