INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470544, 'Fosphenytoin', 'Oliceridine', 'Major', 'Coadministration with enzyme-inducing anticonvulsants may decrease the plasma concentrations of opioids that are primarily metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of an enzyme-inducing anticonvulsant. Conversely, discontinuation of the anticonvulsant may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317476, 'Pharmacologic response to the opioid should be monitored more closely whenever an enzyme-inducing anticonvulsant is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary. Due to the potential for additive central nervous system depressant effects, ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them.', 'Metabolism', 'Pharmacologic response to the opioid should be monitored more closely whenever an enzyme-inducing anticonvulsant is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', NULL, NULL, 'Major', 'pharmacodynamic');
