INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470634, 'Paroxetine', 'Oliceridine', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations and adverse opioid effects of oliceridine, which is primarily metabolized by both isoenzymes in vitro. In addition, oliceridine concentrations may decrease following discontinuation of a concomitant moderate or potent CYP450 2D6 or 3A4 inhibitor, possibly resulting in decreased opioid efficacy or withdrawal syndrome. The effect of a CYP450 2D6 inhibitor on the pharmacokinetics of oliceridine has not been studied. However, it may be similar to that reported in CYP450 2D6 poor metabolizers, whose plasma clearance of oliceridine is reduced by approximately 50% compared to those who are nonpoor CYP450 2D6 metabolizers.', 'DDInter', 1767317476, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4. If concomitant use is considered necessary, patients may require less frequent dosing of oliceridine and should be closely monitored for respiratory depression, sedation, and QT prolongation. If a moderate or potent CYP450 2D6 or 3A4 inhibitor is discontinued, an increase of the oliceridine dosage may be considered while monitoring for signs of opioid withdrawal until stable drug effects are achieved.', 'Metabolism', 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.', NULL, NULL, 'Major', 'pharmacodynamic');
