INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244564, 'Lopinavir', 'Lamotrigine', 'Moderate', 'Coadministration with lopinavir/ritonavir may decrease the serum concentrations of lamotrigine.  The mechanism is increased clearance due to induction of lamotrigine glucuronidation in the liver.', NULL, 'When lamotrigine is added to existing therapy containing an enzyme-inducing agent without valproate, the initial dosage of lamotrigine should be 0.', NULL, 'When lamotrigine is added to existing therapy containing an enzyme-inducing agent without valproate, the initial dosage of lamotrigine should be 0.6 mg/kg/day in two divided doses (2 to 12 years of age) or 50 mg/day (older than 12 years of age) for the first 2 weeks.  The initial dosage should be doubled for the next 2 weeks, then increased by 1.2 mg/kg/day or 100 mg/day every 1 to 2 weeks as needed and as tolerated.  The usual maintenance dosage is 5 to 15 mg/kg/day (up to 400 mg/day) in children up to 12 years of age and 300 to 500 mg/day (400 mg/day for the treatment of bipolar disorder) in older patients.  Patients should be advised to promptly notify their physician if they experience worsening of seizure control or increased adverse effects.  A reevaluation of all antiepileptic agents in the regimen should be considered prior to making any changes.  If the enzyme-inducing agent is discontinued, lamotrigine half-life will be prolonged and a dosage adjustment may be necessary.  Prescribers should refer to the lamotrigine product labeling for complete dosing information.', 'Excretion', 'Brivaracetam, Topiramate, Ethosuximide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288378/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
