INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244584, 'Lopinavir', 'Loperamide', 'Moderate', 'The use of higher than recommended dosages of loperamide (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including syncope, cardiac arrest, and arrhythmia related to prolongation of the QT interval.  Under such circumstances, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if loperamide is used in combination with other drugs that can prolong the QT interval.  Patients should be counseled to not exceed the recommended dosage and frequency or duration of use of loperamide, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.  If loperamide-induced cardiotoxicity is suspected, promptly discontinue loperamide and initiate therapy to manage and prevent cardiac arrhythmias and adverse outcomes.  Electrical pacing or cardioversion may be necessary if torsade de pointes persists despite pharmacotherapy.  In many of the reported cases of loperamide-induced cardiotoxicity, standard antiarrhythmic drugs were ineffective, and electrical pacing or cardioversion was necessary.', 'Synergism', 'Opium, Diphenoxylate, Morphine, Difenoxin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Etravirine, Dolutegravir, Oseltamivir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288402/', '[1] Eggleston W, Clark KH, Marraffa JM "Loperamide abuse associated with cardiac dysrhythmia and death." Ann Emerg Med 69 (2017):  83-6[2] US Food and Drug Administration "FDA warns about serious heart problems with high doses of the antidiarrheal medicine loperamide (Imodium), including from abuse and misuse.  http://www.fda.gov/downloads/Drugs/DrugSafety/UCM505108.pdf"   (2016):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244585, 'Lorlatinib', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288403/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[3] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[4] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004):  482-9[5] Lim ML, Min SS, Eron JJ, et al. "Coadministration of lopinavir/ritonavir and phenytoin results in two-way drug interaction through cytochrome P-450 induction." J Acquir Immune Defic Syndr 36 (2004):  1034-40', 1767369485);
