INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244604, 'Mineral oil', 'Lopinavir', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.  The recommended dosage and duration of use should not be exceeded.  Patients treated with lactulose for more than six months should be monitored periodically for electrolyte imbalance.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Naldemedine, Methylnaltrexone, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, Prucalopride, Linaclotide, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288424/', '[1] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998):  517-8[2] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993):  138-45[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Asso