INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244605, 'Mirtazapine', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir may cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.  Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288425/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[2] Anson BD, Weaver JG, Ackerman MJ, et al. "Blockade of HERG channels by HIV protease inhibitors." Lancet 365 (2005):  682-686[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244606, 'Mitotane', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288426/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[3] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[4] Liedtke MD, Lockhart SM, Rathbun RC "Anticonvulsant and antiretroviral interactions." Ann Pharmacother 38 (2004):  482-9[5] Lim ML, Min SS, Eron JJ, et al. "Coadministration of lopinavir/ritonavir and phenytoin results in two-way drug interaction through cytochrome P-450 induction." J Acquir Immune Defic Syndr 36 (2004):  1034-40', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244607, 'Modafinil', 'Lopinavir', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of protease inhibitors (PIs).  The mechanism involves induction of intestinal CYP450 3A4-mediated first-pass metabolism by modafinil and armodafinil, with possible secondary induction of hepatic 3A4 metabolism.  Significant intestinal metabolism appears to occur with indinavir and saquinavir but has not been studied with other PIs.  Conversely, PIs may increase the plasma concentrations of modafinil via inhibition of hepatic CYP450 3A4 metabolism.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with PIs, particularly if they are using only one PI in their antiretroviral regimen.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with decreased or subtherapeutic antiretroviral drug levels, caution is advised if modafinil or armodafinil must be used in patients treated with PIs, particularly if they are using only one PI in their antiretroviral regimen.  Clinical monitoring for altered effects of both modafinil and the PI may be appropriate following addition or withdrawal of one or the other drug.', 'Metabolism', 'Amphetamine, Pemoline, Metamfetamine, Solriamfetol, Caffeine, Methylphenidate, Dexmethylphenidate, Dextroamphetamine, Citicoline', 'Lamivudine, Paritaprevir, Cabotegravir, Simeprevir, Zanamivir, Velpatasvir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288428/', '[1] Barry M, Gibbons S, Back D, Mulcahy F "Protease inhibitors in patients with HIV disease. Clinically important pharmacokinetic considerations." Clin Pharmacokinet 32 (1997):  194-209[2] "Product Information. Provigil (modafinil)." Cephalon, Inc  (2001):[3] Sommadossi JP "HIV protease inhibitors: pharmacologic and metabolic distinctions." AIDS 13 (1999):  s29-40[4] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000):  1333-9[5] Robertson P, Decory HH, Madan A, Parkinson A "In vitro inhibition and induction of human hepatic cytochrome P450 enzymes by modafinil." Drug Metab Dispos 28 (2000):  664-71[6] Doherty MM, Charman WN "The mucosa of the small intestine: how clinically relevant as an organ of drug metabolism?" Clin Pharmacokinet 41 (2002):  235-53[7] "Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244608, 'Moricizine', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme.  The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Lidocaine, Tocainide, Propafenone, Mexiletine, Bretylium', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288429/