INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244610, 'Nadolol', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients.  Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, 'Caution is advised if lopinavir-ritonavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., calcium channel blockers) because lopinavir-ritonavir is a potent inhibitor of the isoenzyme.  The elderly as well as patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block associated with the use of lopinavir-ritonavir.  Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288431/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244611, 'Nafcillin', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288432/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P