INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244694, 'Rosuvastatin', 'Lopinavir', 'Major', 'Coadministration with protease inhibitors may significantly increase the plasma concentrations of rosuvastatin.  The mechanism may involve inhibition of OATP1B1-mediated hepatic uptake and/or BCRP-mediated intestinal and hepatobiliary efflux.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, 'The benefits of using rosuvastatin with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.  If coadministration is required, rosuvastatin should be initiated at the lowest effective dosage and not exceed 10 mg per day when prescribed with lopinavir-ritonavir, atazanavir-ritonavir, atazanavir-cobicistat, or ombitasvir/paritaprevir/ritonavir plus dasabuvir.  The dosage of rosuvastatin should be limited to 20 mg once a day when used with darunavir-cobicistat.  Nirmatrelvir-ritonavir product labeling advises temporary discontinuation of rosuvastatin be considered during treatment with nirmatrelvir-ritonavir.  Alternatively, a different HMG-CoA reductase inhibitor such as fluvastatin may be considered.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Distribution', 'Ramipril, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288521/', '[1] "Product Information. Invirase (saquinavir)." Roche Laboratories  (2001):[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical  (2001):[3] "Product Information. Crixivan (indinavir)." Merck & Co., Inc  (2001):[4] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[5] "Product Information. Crestor (rosuvastatin)." AstraZeneca Pharma Inc  (2003):[6] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb  (2003):[7] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline  (2003):[8] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim  (2005):[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Prezista (darunavir)." Ortho Biotech Inc  (2006):[11] Hirano M, Maeda K, Shitara Y, Sugiyama Y "Drug-drug interaction between pitavastatin and various drugs via OATP1B1." Drug Metab Dispos 34 (2006):  1229-36[12] Neuvonen PJ, Niemi M, Backman JT "Drug interactions with lipid-lowering drugs: Mechanisms and clinical relevance." Clin Pharmacol Ther 80 (2006):  565-81[13] Cerner Multum, Inc. "Australian Product Information." O 0[14] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):[15] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb  (2015):[16] US Food and Drug Administration "FACT SHEET FOR HEALTHCARE PROVIDERS EMERGENCY USE AUTHORIZATION FOR PAXLOVID.  https://www.fda.gov/media/155050/download"   (2021):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244695, 'Rucaparib', 'Lopinavir', 'Moderate', 'Lopinavir in combination with ritonavir may cause dose-related prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.  Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288522/', '[1] "Product Information. Kaletra (lopinavir-ritonavir)." Abbott Pharmaceutical  (2001):[2] Anson BD, Weaver JG, Ackerman MJ, et al. "Blockade of HERG channels by HIV protease inhibitors." Lancet 365 (2005):  682-686[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244696, 'Rufinamide', 'Lopinavir', 'Moderate', 'Coadministration of lopinavir-ritonavir with inducers of CYP450 3A4 may decrease the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme.  Clinical studies have shown that potent CYP450 3A4 inducers such as rifampin and phenytoin can significantly alter the plasma concentrations of lopinavir, possibly by overriding some of the inhibiting effects of ritonavir and enhancing the clearance of both lopinavir and ritonavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if lopinavir-ritonavir is prescribed with CYP450 3A4 inducers.  Close clinical and laboratory monitoring of antiretroviral response is recommended.', 'Metabolism', 'Brivaracetam, Topiramate, Ethosuximide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, Ganaxolone, Vigabatrin, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Etravirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288523/', '[1] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997):  87,90[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Impo