INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244770, 'St. John''s Wort', 'Nelfinavir', 'Major', 'Coadministration with St. John''s wort may significantly reduce the plasma concentrations of HIV protease inhibitors and result in a potential loss of virologic response.  The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of HIV protease inhibitors with St. John''s wort is considered contraindicated.  The enzyme induction may last for up to 2 weeks after the last dose of St. John''s wort.  Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Lamivudine, Paritaprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, More', 'Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, Esketamine, Phenelzine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288610/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244771, 'Telotristat ethyl', 'Nelfinavir', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Cobicistat, Acyclovir, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288611/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc.  (2017):', 1767369485);
