INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244809, 'Alprazolam', 'Benzhydrocodone', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them.  Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients using benzodiazepines or other CNS depressants including alcohol.  For patients who have been receiving extended therapy with both an opioid and a benzodiazepine and require discontinuation of either medication, a gradual tapering of dose is advised, since abrupt withdrawal may lead to withdrawal symptoms.  Severe cases of benzodiazepine withdrawal, primarily in patients who have received excessive doses over a prolonged period, may result in numbness and tingling of extremities, hypersensitivity to light and noise, hallucinations, and epileptic seizures.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288655/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning.  http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf"   (2016):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (244810, 'Alvimopan', 'Benzhydrocodone', 'Major', 'Recent exposure to opioids can increase the sensitivity to mu opioid receptor antagonists.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration.', NULL, 'The use of alvimopan is contraindicated in patients who have received therapeutic doses of opioids for more than 7 consecutive days immediately prior to alvimopan administration.  Caution is advised in patients who have received more than 3 doses of an opioid within the week prior to surgery, as these patients were not studied in the postoperative ileus clinical trials.  Patients with recent exposure to opioids should be monitored for increased adverse effects such as abdominal pain, nausea and vomiting, and diarrhea.', 'Synergism', 'Sorbitol, Tegaserod, Naloxegol, Bisacodyl, Naldemedine, Lactulose, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Sodium sulfate, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/288656/', '[1] "Product Information. Entereg (alvimopan)." GlaxoSmithKline  (2008):', 1767369485);
