INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1470712, 'Sildenafil', 'Oliceridine', 'Moderate', 'Acute intake of opiates during sildenafil use may result in prolonged erections. The proposed mechanism is an opiate-induced acute increase in cyclic guanosine monophosphate concentrations in peripheral nerve endings, which can be additive with that produced by sildenafil.', 'DDInter', 1767317476, 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur. The interaction may not occur during chronic use of opiates.', 'Synergism', 'Patients using sildenafil should be advised of the potential for prolonged erections during acute intake of opiates, and to contact their physician if problems occur.', NULL, NULL, 'Moderate', 'pharmacodynamic');
