INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471063, 'Zoledronic acid', 'Olsalazine', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', 'DDInter', 1767317476, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471064, 'Acetohexamide', 'Omacetaxine mepesuccinate', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317476, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471065, 'Acetohydroxamic acid', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471066, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Omacetaxine mepesuccinate', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 1767317476, 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471067, 'Anisindione', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317476, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
