INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471138, 'Heparin', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317476, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471139, 'Hepatitis A Vaccine', 'Omacetaxine mepesuccinate', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
