INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471278, 'Typhoid vaccine (inactivated)', 'Omacetaxine mepesuccinate', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471279, 'Typhoid vaccine (live)', 'Omacetaxine mepesuccinate', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471280, 'Upadacitinib', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of upadacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies.', 'DDInter', 1767317476, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and upadacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs or symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, or pain or burning during urination. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471281, 'Uracil mustard', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 1767317476, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1471282, 'Urokinase', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of omacetaxine and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with omacetaxine is associated with a high frequency of thrombocytopenia.', 'DDInter', 1767317476, 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The CBC and platelet count should be monitored according to the manufacturer''s recommendations.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be avoided if the patient''s platelet count is less than 50,000 per microliter.', NULL, NULL, 'Major', 'pharmacodynamic');
