INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246023, 'Dihydrocodeine', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4.  The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289967/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246024, 'Dihydrocodeine', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates.  The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.  Dose adjustments of the coadministered drug may be required if an interaction is suspected.  The manufacturer also recommends that monitoring should be considered during concomitant use of drugs with a narrow therapeutic index, including digoxin and warfarin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289968/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Banzel (rufinamide)." Eisai Inc  (2008):', 1767363028);
