INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246026, 'Dihydrocodeine', 'Scopolamine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289970/', '[1] "Product Information. Demerol (meperidine)." Sanofi Winthrop Pharmaceuticals  (2002):[2] "Product Information. Dolophine (methadone)." Lilly, Eli and Company  (2002):[3] "Product Information. Tylenol with Codeine (acetaminophen-codeine)." Janssen Pharmaceuticals  (2001):[4] "Product Information. Duragesic Transdermal System (fentanyl)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Ultram (tramadol)." McNeil Pharmaceutical  (2001):[6] "Product Information. OxyContin (oxycodone)." Purdue Frederick Company  (2001):[7] "Product Information. Kadian (morphine)." Astra-Zeneca Pharmaceuticals  (2001):[8] "Product Information. DepoDur (morphine liposomal)." Endo Laboratories LLC  (2004):[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Opana (oxymorphone)." Endo Laboratories LLC  (2006):[11] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals  (2009):[12] "Product Information. Exalgo (hydromorphone)." Covidien  (2010):[13] "Product Information. Belbuca (buprenorphine)." Endo Pharmaceuticals Solutions Inc  (2016):[14] "Product Information. Alfentanil Hydrochloride (alfentanil)." Akorn Inc  (2017):[15] "Product Information. SUFentanil Citrate (sufentanil)." Akorn Inc  (2017):[16] "Product Information. Lortab (acetaminophen-hydrocodone)." Akorn Inc  (2017):[17] "Product Information. Levorphanol Tartrate (levorphanol)." Sentynl Therapeutics  (2017):[18] "Product Information. Naloxone HCl-Pentazocine HCl (naloxone-pentazocine)." Actavis U.S. (Amide Pharmaceutical Inc)  (2018):[19] "Product Information. Apadaz (acetaminophen-benzhydrocodone)." KemPharm, Inc  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246027, 'Dihydrocodeine', 'Secobarbital', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death.  The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.  If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed.  Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them.  Cough medications containing opioids (e.g., codeine, hydrocodone) should not be prescribed to patients using benzodiazepines or other CNS depressants including alcohol.  For patients who have been receiving extended therapy with both an opioid and a benzodiazepine and require discontinuation of either medication, a gradual tapering of dose is advised, since abrupt withdrawal may lead to withdrawal symptoms.  Severe cases of benzodiazepine withdrawal, primarily in patients who have received excessive doses over a prolonged period, may result in numbness and tingling of extremities, hypersensitivity to light and noise, hallucinations, and epileptic seizures.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289971/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning.  http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf"   (2016):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246028, 'Dihydrocodeine', 'Selegiline', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs), such as selegiline (an MAO-B inhibitor), with opioid analgesics may result in serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5HT1A receptors.', NULL, 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.', NULL, 'Concomitant use of MAOIs, such as selegiline, with opioid analgesics should generally be avoided.  At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with an opioid analgesic.  If coadministration is unavoidable, patients should be monitored for signs and symptoms of serotonin syndrome which may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.  Some authorities consider the use of selegiline with opioid analgesics to be contraindicated (UK).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289972/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Zelapar (selegiline)." Valeant Pharmaceuticals  (2006):[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246029, 'Dihydrocodeine', 'Selpercatinib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by selpercatinib.  The interaction may be particularly important for sensitive substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.  Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289973/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company  (2020):', 1767363028);
