INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246044, 'Dihydrocodeine', 'St. John''s Wort', 'Moderate', 'Coadministration of codeine or dihydrocodeine with CYP450 3A4 inducers such as St. John''s Wort may result in lower codeine or dihydrocodeine plasma concentrations, higher levels of the inactive metabolite norcodeine or dihydronorcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine or dihydromorphine levels, respectively.', NULL, 'Caution and clinical monitoring are advised if codeine or dihydrocodeine are to be used in combination with St.', NULL, 'Caution and clinical monitoring are advised if codeine or dihydrocodeine are to be used in combination with St. John''s Wort, particularly when it is added to or withdrawn from therapy.  Dosage adjustments may be required.  Some manufacturers of products containing codeine recommend against using codeine concomitantly with CYP450 3A4 inducers.  Patients should be advised to monitor for symptoms of serotonin excess or serotonin syndrome.  If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately, and supportive care rendered as necessary.  Patients should also be advised to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these medications affect them.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/289990/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Codeine Sulfate (codeine)." Lannett Company Inc  (2015):[4] "Product 