INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246156, 'Chromium picolinate', 'Liotrix', 'Moderate', 'Concurrent administration of chromium picolinate-containing oral products may decrease the oral bioavailability of levothyroxine in some patients.  The exact mechanism has not been elucidated but may involve nonspecific adsorption of levothyroxine to polyvalent cations, resulting in an insoluble complex that that is poorly absorbed from the gastrointestinal tract.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and chromium picolinate by either taking oral levothyroxine at least 30 minutes before or three to four hours after taking chromium picolinate-containing oral preparations.', NULL, 'Some experts recommend separating the times of administration of levothyroxine and chromium picolinate by either taking oral levothyroxine at least 30 minutes before or three to four hours after taking chromium picolinate-containing oral preparations.  Monitoring of serum TSH levels is recommended.  Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290387/', '[1] John-Kalarickal J, Pearlman G, Carlson HE "New medications which decrease levothyroxine absorption" Thyroid 17 (2007):  763-765[2] Wiesner A, Gajewska D, Pasko P "Levothyroxine interactions with food and dietary supplements - a systematic review" Pharmaceuticals (Basel) 14 (2021):  206', 1767363028);
