INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246224, 'Budesonide', 'Somapacitan', 'Moderate', 'Human growth hormone may reduce the pharmacologic effects of corticosteroids.  Growth hormone is thought to enhance the activity of CYP450 3A4, an enzyme involved in the catabolism of corticosteroids.  In addition, growth hormone (GH) inhibits microsomal enzyme 11-beta-hydroxysteroid dehydrogenase type 1 (11BHSD-1), which is required for conversion of cortisone to its active metabolite, cortisol, in hepatic and adipose tissue.  Consequently, individuals with untreated GH deficiency have relative increases in 11BHSD-1 and serum cortisol, and initiation of GH therapy may result in inhibition of 11BHSD-1 and reduced serum cortisol levels.', NULL, 'Higher dosages of corticosteroids may be required during concomitant use of human growth hormone.', NULL, 'Higher dosages of corticosteroids may be required during concomitant use of human growth hormone.  Patients treated with glucocorticoid replacement for previously diagnosed hypoadrenalism may require an increase in their maintenance or stress doses following initiation of GH treatment.  In addition, glucocorticoid replacement dosing should be carefully adjusted in children receiving concomitant GH treatment to avoid both hypoadrenalism and an inhibitory effect on growth.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290475/', '[1] "Product Information. Nutropin (somatropin)." Genentech  (2001):[2] "Product Information. Protropin (somatrem)." Genentech  (2001):[3] "Product Information. Norditropin Cartridge (somatropin)." Novo Nordisk Pharmaceuticals Inc  (2004):[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
