INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246245, 'Avapritinib', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of avapritinib, which has been shown in vitro to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C9.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of avapritinib with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends reducing the starting dosage of avapritinib from 300 mg orally once daily to 100 mg orally once daily.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290502/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ayvakit (avapritinib)." Blueprint Medicines Corporation  (2020):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246246, 'Axitinib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.  Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible.  Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290503/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Inlyta (axitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246247, 'Bepridil', 'Netupitant', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290505/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767363028);
