INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246269, 'Cevimeline', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of cevimeline, which is metabolized by these isoenzymes.', NULL, 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.', NULL, 'Dosage adjustments and clinical monitoring may be appropriate whenever a CYP450 2D6 and/or 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to notify their physician if they experience excessive adverse cholinergic effects of cevimeline such as nausea, vomiting, diarrhea, sweating, salivation, urinary frequency, visual disturbance, confusion, tremor, palpitations, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290529/', '[1] "Product Information. Evoxac (cevimeline)." Daiichi Pharmaceuticals  (2001):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246270, 'Chloroquine', 'Netupitant', 'Moderate', 'Coadministration with moderate or strong inhibitors of CYP450 2C8 or CYP450 3A4 may increase the plasma concentration of hydroxychloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.  Following coadministration with cimetidine, a weak to moderate CYP450 3A4 inhibitor, a 2-fold increase in chloroquine exposure occurred.  Because chloroquine and hydroxychloroquine have similar structures and metabolic elimination pathways, a similar interaction could be observed for hydroxychloroquine.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a moderate or strong CYP450 3A4 or 2C8 inhibitor.  Clinical and laboratory monitoring should be considered whenever a moderate or strong CYP450 3A4 or 2C8 inhibitor is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary.  Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290530/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246271, 'Cilostazol', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C19 may increase the plasma concentrations of cilostazol and or its pharmacologically active metabolites, which are substrates of these isoenzymes.', NULL, 'A 50% dosage reduction of cilostazol (i.', NULL, 'A 50% dosage reduction of cilostazol (i.e., 50 mg twice a day) should be considered when used with potent or moderate CYP450 3A4 and/or 2C19 inhibitors.  Close clinical and laboratory monitoring is advised whenever the inhibitor is added to or withdrawn from therapy, and the cilostazol dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience adverse effects of cilostazol such as dizziness, nausea, diarrhea, bleeding, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290532/', '[1] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals Inc  (2001):[2] Suri A, Bramer SL "Effect of omeprazole on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999):  53-9[3] Suri A, Forbes WP, Bramer SL "Effects of CYP3A inhibition on the metabolism of cilostazol." Clin Pharmacokinet 37 (1999):  61-8[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246272, 'Cisapride', 'Netupitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cisapride, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with potent CYP450 3A4 inhibitors (e.', NULL, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the concomitant use with potent CYP450 3A4 inhibitors (e.g., azole antifungal agents, protease inhibitors, certain macrolide antibiotics, nefazodone) is considered contraindicated.  Use with other agents known to inhibit CYP450 3A4 should generally be avoided if possible.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290533/', '[1] "Product Information. Propulsid (cisapride)." Janssen Pharmaceuticals  (2001):[2] Bran S, Murray WA, Hirsch IB, Palmer JP "Long QT syndrome during high-dose cisapride." Arch Intern Med 155 (1995):  765-8[3] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996):  279-81[4] Bedford TA, Rowbotham DJ "Cisapride: drug interactions of clinical significance." Drug Saf 15 (1996):  167-75[5] Michalets EL "Update: clinically significant cytochrome P-450 drug interactions." Pharmacotherapy 18 (1998):  84-112[6] Hill SL, Evangelista JK, Pizzi AM, Mobassaleh M, Fulton DR, Berul CI "Proarrhythmia associated with cisapride in children." Pediatrics 101 (1998):  1053-6[7] Gray VS "Syncopal episodes associated with cisapride and concurrent drugs." Ann Pharmacother 32 (1998):  648-51[8] Thomas AR, Chan LN, Bauman JL, Olopade CO "Prolongation of the QT interval related to cisapride-diltiazem interaction." Pharmacotherapy 18 (1998):  381-5[9] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000):  41-57[10] Desta Z, Soukhova N, Mahal SK, Flockhart DA "Interaction of cisapride with the human cytochrome P450 system: metabolism and inhibition studies." Drug Metab Dispos 28 (2000):  789-800[11] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000):  49-75', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246273, 'Citalopram', 'Netupitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme.  Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.  Likewise, coadministration of a single 20 mg dose of escitalopram with a single 600 mg dose of ritonavir, another potent CYP450 3A4 inhibitor, did not affect the pharmacokinetics of either drug.  Because citalopram and escitalopram are both metabolized by multiple isoenzymes (CYP450 2C19, 3A4, and 2D6), inhibition of a single isoenzyme may not appreciably decrease their clearance.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290534/', '[1] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):[2] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals  (2002):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246274, 'Clarithromycin', 'Netupitant', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of netupitant.', NULL, 'No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.', NULL, 'No dosage adjustment is necessary for single-dose administration of netupitant-palonosetron when used with potent CYP450 3A4 inhibitors.  However, patients may experience increased adverse effects such as headache, asthenia, dyspepsia, fatigue, constipation, and erythema.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290535/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246275, 'Clonazepam', 'Netupitant', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290536/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767363028);
