INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246333, 'Etravirine', 'Netupitant', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of the other drug.  Etravirine itself is a substrate and inducer of CYP450 3A4.  Theoretically, etravirine may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.  In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290599/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc  (2008):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246334, 'Everolimus', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration.  Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', NULL, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.', NULL, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.  The manufacturer''s drug product labeling should be consulted for specific recommendations.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290600/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals  (2009):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246335, 'Fedratinib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fedratinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19 and flavin-containing monooxygenase 3 (FMO3).', NULL, 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.', NULL, 'Caution is advised when fedratinib is used with CYP450 3A4 inhibitors, particularly those that may also inhibit CYP450 2C19 (e.g., amiodarone, cimetidine, fexinidazole, isoniazid, osilodrostat, rucaparib) or if combined with other drugs that can inhibit CYP450 2C19 (e.g., esomeprazole, fenofibrate, fluoxetine, lansoprazole, letrozole, moclobemide, omeprazole, parecoxib, ticlopidine).  Patients should be monitored for adverse reactions such as nausea, vomiting, diarrhea, anemia, thrombocytopenia, neutropenia, encephalopathy (including Wernicke''s), liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations, increased blood creatinine, and secondary malignancies.  Dosage adjustment or treatment interruption may be required based on patient tolerance and response in accordance with the prescribing information.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290601/', '[1] Wu F, Krishna G, Surapaneni S "Physiologically based pharmacokinetic modeling to assess metabolic drug-drug interaction risks and inform the drug label for fedratinib." Cancer Chemother Pharmacol 86 (2020):  461-73[2] "Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb  (2022):[3] "Product Information. Inrebic (fedratinib)." Bristol-Myers Squibb Pharmaceuticals Ltd  (2021):', 1767363028);
