INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246378, 'Netupitant', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.  Patients should be monitored more frequently for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted based on severity of emergent adverse reactions in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290650/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc  (2022):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246379, 'Netupitant', 'Lefamulin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-gp may increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.  When oral lefamulin was administered with oral ketoconazole, a potent CYP450 3A4/P-gp inhibitor, mean lefamulin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by 58% and 165%, respectively.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.  Patients should be monitored for increased adverse effects and seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290651/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc.  (2019):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246380, 'Netupitant', 'Lemborexant', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290652/', '[1] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.[2] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246381, 'Netupitant', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy.  Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine.  The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor.  Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290655/', '[1] "Product Information. Prevymis (letermovir)." Merck & Co., Inc  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246382, 'Netupitant', 'Letrozole', 'Moderate', 'Coadministration with netupitant or its prodrug, fosnetupitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by netupitant and its desmethyl metabolite.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when netupitant or fosnetupitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever netupitant or fosnetupitant is added to or withdrawn from therapy.  The inhibitory effect on CYP450 3A4 can last for 6 days after administration of a single dose of netupitant or fosnetupitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290656/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc  (2014):[3] "Product Information. Akynzeo for Injection (fosnetupitant-palonosetron)." Helsinn Therapeutics Inc  (2018):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246383, 'Netupitant', 'Levamlodipine', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', NULL, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.  Dosage reduction may be required for amlodipine.  Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290657/', '[1] "Product Information. Norvasc (amlodipine)." Pfizer U.S. Pharmaceuticals  (2002):[2] Sasaki M, Maeda A, Fujimura A "Influence of diltiazem on the pharmacokinetics of amlodipine in elderly hypertensive patients." Eur J Clin Pharmacol 57 (2001):  85-6[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS.  http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink"   (2006):[5] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246384, 'Netupitant', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil).  Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine).  Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290658/', '[1] "Product Information. Chirocaine (levobupivacaine)." Organon  (2001):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363028);
