INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246406, 'Netupitant', 'Midostaurin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', NULL, 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors.  Patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290684/', '[1] Dutreix C, Munarini F, Lorenzo S, Roesel J, Wang Y "Investigation into CYP3A4-mediated drug-drug interactions on midostaurin in healthy volunteers." Cancer Chemother Pharmacol 72 (2013):  1223-34[2] "Product Information. Rydapt (midostaurin)." Novartis Pharmaceuticals  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246407, 'Netupitant', 'Mifepristone', 'Moderate', 'Coadministration of mifepristone with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of both drugs.  Mifepristone itself is also a substrate and moderate inhibitor of CYP450 3A4.  Theoretically, competitive and/or noncompetitive metabolic inhibition may occur.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with a CYP450 3A4 inhibitor, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', NULL, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with a CYP450 3A4 inhibitor, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.  When the coadministered drug is also a CYP450 3A4 substrate, clinical and/or laboratory monitoring may be appropriate following the addition or withdrawal of mifepristone, and the dosage adjusted as necessary.  Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290685/', '[1] "Product Information. Mifeprex (mifepristone)." Danco Laboratories  (2001):[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated  (2012):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246408, 'Netupitant', 'Mirabegron', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme.  However, in vivo results indicate that CYP450 3A4 may play a limited role in the overall elimination.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme.', NULL, 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme.  However, in vivo results indicate that CYP450 3A4 may play a limited role in the overall elimination.  In a study consisting of 23 healthy male and female subjects, mirabegron peak plasma concentration (Cmax) increased by 45% and systemic exposure (AUC) increased by 80% when a single 100 mg dose of mirabegron was administered following multiple dosing of ketoconazole 400 mg daily for 9 days.  No dosage adjustment is recommended when mirabegron is administered in combination with ketoconazole and probably other CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290686/', '[1] "Product Information. Myrbetriq (mirabegron)." Astellas Pharma US, Inc  (2012):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246409, 'Netupitant', 'Mirtazapine', 'Moderate', 'Coadministration of mirtazapine with drugs that inhibit one or more of its metabolic pathways may result in increased plasma concentrations of mirtazapine.  In vitro data from human liver microsomes indicate that CYP450 2D6 and 1A2 are involved in the formation of the 8-hydroxy metabolite, while CYP450 3A4 is primarily responsible for the formation of the N-desmethyl and N-oxide metabolites.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.  Therapeutic response to mirtazapine should be monitored more closely following initiation, discontinuation, or dosing change of CYP450 inhibitors, and the mirtazapine dosage adjusted as necessary.  A prolonged duration of monitoring for adverse effects may be required depending on the elimination half-life of the concomitant drug.  For example, it should be noted that rolapitant, a moderate CYP450 2D6 inhibitor, can increase plasma concentrations and the risk of adverse effects of mirtazapine for at least 28 days after administration of rolapitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290687/', '[1] "Product Information. Remeron (mirtazapine)." Organon  (2001):[2] Sitsen JM, Maris FA, Timmer CJ "Concomitant use of mirtazapine and cimetidine: a drug-drug interaction study in healthy male subjects." Eur J Clin Pharmacol 56 (2000):  389-94[3] Okubo M, Murayama N, Miura J, Chiba Y, Yamazaki H "Effects of cytochrome P450 2D6 and 3A5 genotypes and possible coadministered medicines on the metabolic clearance of antidepressant mirtazapine in Japanese patients." Biochem Pharmacol 93 (2015):  104-9[4] "Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):[5] Stormer E, von Moltke LL, Shader RI, Greenblatt DJ "Metabolism of the antidepressant mirtazapine in vitro: contribution of cytochromes P-450 1A2, 2D6, and 3A4" Drug Metab Dispos 28 (2000):  1168-75', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246410, 'Netupitant', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.  Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4.', NULL, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', NULL, 'Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.  Dose adjustments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290691/', '[1] "Product Information. Provigil (modafinil)." Cephalon, Inc  (2001):[2] "Product Information. Nuvigil (armodafinil)." Cephalon Inc  (2007):[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246411, 'Netupitant', 'Naldemedine', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G.  Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.  Naldemedine is also a substrate of the P-gp efflux transporter.', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).  Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290692/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Symproic (naldemedine)." Shionogi USA Inc  (2017):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246412, 'Netupitant', 'Naloxegol', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided.  If coadministration is required, the dosage of naloxegol should be reduced to 12.5 mg once daily and the patient closely monitored for opioid withdrawal symptoms.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290693/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Movantik (naloxegol)." Astra-Zeneca Pharmaceuticals  (2014):', 1767363028);
