INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246415, 'Netupitant', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', NULL, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.  According to some authorities, if the CYP450 3A4 inhibitor cannot be avoided, the dose of neratinib should be reduced to 40 mg once daily with a strong CYP450 3A4 inhibitor and 200 mg once daily with a moderate CYP450 3A4 inhibitor.  The previous dose of neratinib may be resumed following discontinuation of a strong or moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290696/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011):  522-7[3] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc.  (2017):', 1767363028);
