INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246425, 'Olaparib', 'Netupitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme.', NULL, 'MANAGEMENT Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, 'MANAGEMENT Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.  If coadministration is required, dose reductions are recommended as follows:  the olaparib tablet dosage should be reduced to 150 mg twice a day; the olaparib capsule dosage should be reduced to 200 mg twice a day.  Specific dose recommendations for each formulation should be followed as the tablet and capsule formulations are not interchangeable due to differences in bioavailability.  Once the CYP450 3A4 inhibitor has been discontinued for 3 to 5 elimination half-lives, the usual olaparib dose should be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290706/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Lynparza (olaparib)." Astra-Zeneca Pharmaceuticals  (2014):', 1767363028);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246426, 'Oliceridine', 'Netupitant', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations and adverse opioid effects of oliceridine, which is primarily metabolized by both isoenzymes in vitro.  In addition, oliceridine concentrations may decrease following discontinuation of a concomitant moderate or potent CYP450 2D6 or 3A4 inhibitor, possibly resulting in decreased opioid efficacy or withdrawal syndrome.', NULL, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.', NULL, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.  If concomitant use is considered necessary, patients may require less frequent dosing of oliceridine and should be closely monitored for respiratory depression, sedation, and QT prolongation.  If a moderate or potent CYP450 2D6 or 3A4 inhibitor is discontinued, an increase of the oliceridine dosage may be considered while monitoring for signs of opioid withdrawal until stable drug effects are achieved.  Due to the prolonged duration of CYP450 2D6 inhibition by the moderate CYP450 2D6 inhibitor rolapitant, prolonged monitoring for adverse effects of oliceridine for at least 28 days after administration of rolapitant may be required.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290707/', '[1] "Product Information. Varubi (rolapitant)." Tesaro Inc.  (2015):[2] "Product Information. Olinvyk (oliceridine)." Trevena Inc  (2020):', 1767363028);
