INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246435, 'Pazopanib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.  Pharmacologic response to pazopanib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the pazopanib dosage adjusted as necessary.  Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.  In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290721/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline  (2009):', 1767363028);
