INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (246437, 'Pexidartinib', 'Netupitant', 'Major', 'Coadministration of pexidartinib with moderate CYP450 3A4 inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity.  The proposed mechanism is inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of pexidartinib.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.', NULL, 'The use of pexidartinib with a moderate CYP450 3A4 inhibitor should generally be avoided.  If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations.  If concomitant use of a moderate CYP450 3A4 inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the moderate CYP450 3A4 inhibitor, to the dose that was used prior to starting the moderate CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/290723/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc.  (2019):', 1767363028);
